(this.webpackJsonpiwantit=this.webpackJsonpiwantit||[]).push([[11],{432:function(i,e,t){"use strict";t.r(e),e.default={id:2155954342,category_id:6989,title:"Huawei P30 Lite 6GB 256GB Dual",slug_path:"2155954342/huawei-p30-lite-6gb-256gb-dual",image_url:"https://abpcdn.pstatic.gr/P/bpimg28/3t7uQ/1JecDq_SX576Y1200/0/2155954342.webp",price:24200,excerpt:'<b>Huawei</b>, \u039f\u03b8\u03cc\u03bd\u03b7: 6.15", Android 9, RAM: 6GB, \u039c\u03bd\u03ae\u03bc\u03b7: 256GB, \u039a\u03ac\u03bc\u03b5\u03c1\u03b1: 48MP + 8MP + 2MP, Selfie: 24MP, CPU: \u039f\u03ba\u03c4\u03b1\u03c0\u03cd\u03c1\u03b7\u03bd\u03bf\u03c2 (4+4)',description:'<p>\u03a4\u03c1\u03b9\u03c0\u03bb\u03ae \u03ba\u03ac\u03bc\u03b5\u03c1\u03b1, \u03b5\u03bd\u03c4\u03c5\u03c0\u03c9\u03c3\u03b9\u03b1\u03ba\u03ae \u03c3\u03c7\u03b5\u03b4\u03af\u03b1\u03c3\u03b7, \u03b1\u03c0\u03bf\u03c4\u03b5\u03bb\u03b5\u03c3\u03bc\u03b1\u03c4\u03b9\u03ba\u03cc\u03c2 \u03b5\u03c0\u03b5\u03be\u03b5\u03c1\u03b3\u03b1\u03c3\u03c4\u03ae\u03c2 \u03ba\u03b1\u03b9 \u03bc\u03af\u03b1 \u03bc\u03b5\u03b3\u03ac\u03bb\u03b7 \u03bf\u03b8\u03cc\u03bd\u03b7 6,15 \u03b9\u03bd\u03c4\u03c3\u03ce\u03bd. \u03a4\u03bf <strong>Huawei P30 Lite</strong> \u03ba\u03bf\u03c3\u03c4\u03af\u03b6\u03b5\u03b9 \u03c0\u03bf\u03bb\u03cd \u03bb\u03b9\u03b3\u03cc\u03c4\u03b5\u03c1\u03b1 \u03b1\u03c0\u03cc \u03c4\u03b1 <a href="https://www.bestprice.gr/item/2155571281/huawei-p30-6gb-128gb-dual.html" title="Huawei P30">P30</a> \u03ba\u03b1\u03b9 <a href="https://www.bestprice.gr/item/2155571144/huawei-p30-pro-8gb-128gb-dual.html" title="Huawei P30">P30 Pro</a>, \u03b1\u03bb\u03bb\u03ac \u03b5\u03af\u03bd\u03b1\u03b9 \u03ac\u03be\u03b9\u03bf \xab\u03c4\u03ad\u03ba\u03bd\u03bf\xbb \u03c4\u03b7\u03c2 \u03bf\u03b9\u03ba\u03bf\u03b3\u03ad\u03bd\u03b5\u03b9\u03b1\u03c2. \u03a0\u03b1\u03c1\u03b1\u03ba\u03ac\u03c4\u03c9 \u03bc\u03c0\u03bf\u03c1\u03b5\u03af\u03c2 \u03bd\u03b1 \u03b4\u03b5\u03b9\u03c2 \u03b3\u03b9\u03b1\u03c4\u03af...</p>\n<p>\u0395\u03be\u03c9\u03c4\u03b5\u03c1\u03b9\u03ba\u03ac \u03b5\u03af\u03bd\u03b1\u03b9 \u03c0\u03bf\u03bb\u03cd \u03b4\u03cd\u03c3\u03ba\u03bf\u03bb\u03bf \u03bd\u03b1 \u03be\u03b5\u03c7\u03c9\u03c1\u03af\u03c3\u03b5\u03b9\u03c2 \u03c4\u03b7\u03bd \u03ad\u03ba\u03b4\u03bf\u03c3\u03b7 Lite \u03b1\u03c0\u03cc \u03c4\u03b1 \u03c0\u03b9\u03bf premium \u03bc\u03bf\u03bd\u03c4\u03ad\u03bb\u03b1 \u03c4\u03b7\u03c2 \u03c3\u03b5\u03b9\u03c1\u03ac\u03c2 - \u03ba\u03b1\u03b9 \u03b1\u03c5\u03c4\u03cc \u03b5\u03af\u03bd\u03b1\u03b9 \u03ba\u03b1\u03bb\u03cc. \u03a4\u03bf P30 Lite \u03b4\u03b9\u03b1\u03b8\u03ad\u03c4\u03b5\u03b9 \u03bc\u03af\u03b1 edge-to-edge \u03bf\u03b8\u03cc\u03bd\u03b7 \u03bc\u03b5 \u03bc\u03af\u03b1 \u03bc\u03b9\u03ba\u03c1\u03ae \u03bf\u03c0\u03ae \u03c4\u03cd\u03c0\u03bf\u03c5 \u03c3\u03c4\u03b1\u03b3\u03cc\u03bd\u03b1\u03c2, \u03b5\u03bd\u03c4\u03c5\u03c0\u03c9\u03c3\u03b9\u03b1\u03ba\u03cc \u03c0\u03af\u03c3\u03c9 \u03c0\u03ac\u03bd\u03b5\u03bb \u03c0\u03bf\u03c5 \u03b1\u03bd\u03c4\u03b1\u03bd\u03b1\u03ba\u03bb\u03ac \u03ad\u03bd\u03c4\u03bf\u03bd\u03b1 \u03c4\u03bf \u03c6\u03c9\u03c2 \u03c0\u03b1\u03c1\u2019 \u03cc\u03c4\u03b9 \u03c0\u03bb\u03b1\u03c3\u03c4\u03b9\u03ba\u03cc \u03ba\u03b1\u03b9 \u03bc\u03b9\u03b1 \u03c4\u03c1\u03b9\u03c0\u03bb\u03ae \u03ba\u03ac\u03bc\u03b5\u03c1\u03b1 \u03c3\u03b5 \u03ba\u03b1\u03c4\u03b1\u03ba\u03cc\u03c1\u03c5\u03c6\u03b7 \u03b4\u03b9\u03ac\u03c4\u03b1\u03be\u03b7. \u039f\u03b9 \u03b4\u03b9\u03b1\u03c3\u03c4\u03ac\u03c3\u03b5\u03b9\u03c2 \u03c4\u03bf\u03c5 \u03b5\u03af\u03bd\u03b1\u03b9 152.9 x 72.7 x 7.4 mm \u03ba\u03b1\u03b9 \u03b5\u03af\u03bd\u03b1\u03b9 \u03c0\u03bf\u03bb\u03cd \u03b5\u03bb\u03b1\u03c6\u03c1\u03cd, \u03b6\u03c5\u03b3\u03af\u03b6\u03b5\u03b9 \u03bc\u03cc\u03bb\u03b9\u03c2 159 gr. \u039a\u03b1\u03b9 \u03bf\u03b9 \u03b4\u03cd\u03bf \u03b5\u03c0\u03b9\u03c6\u03ac\u03bd\u03b5\u03b9\u03b5\u03c2 \u03ad\u03c7\u03bf\u03c5\u03bd \u03b5\u03c0\u03ad\u03bd\u03b4\u03c5\u03c3\u03b7 \u03b3\u03c5\u03b1\u03bb\u03b9\u03bf\u03cd, \u03cc\u03c0\u03c9\u03c2 \u03c4\u03b1 \u03c5\u03c0\u03cc\u03bb\u03bf\u03b9\u03c0\u03b1 P30, \u03ba\u03ac\u03c4\u03b9 \u03c0\u03bf\u03c5 \u03b5\u03bd\u03b9\u03c3\u03c7\u03cd\u03b5\u03b9 \u03c4\u03bf\u03bd premium \u03c7\u03b1\u03c1\u03b1\u03ba\u03c4\u03ae\u03c1\u03b1 \u03c4\u03b7\u03c2 \u03c3\u03c7\u03b5\u03b4\u03af\u03b1\u03c3\u03b7\u03c2.</p>\n<p>\u03a4\u03bf Huawei P30 Lite \u03b5\u03af\u03bd\u03b1\u03b9 \u03b5\u03be\u03bf\u03c0\u03bb\u03b9\u03c3\u03bc\u03ad\u03bd\u03bf \u03bc\u03b5 \u03ad\u03bd\u03b1 \u03bf\u03b9\u03ba\u03bf\u03bd\u03bf\u03bc\u03b9\u03ba\u03cc, \u03c0\u03bb\u03b7\u03bd \u03cc\u03bc\u03c9\u03c2 \u03b1\u03c0\u03bf\u03c4\u03b5\u03bb\u03b5\u03c3\u03bc\u03b1\u03c4\u03b9\u03ba\u03cc, <strong>IPS LCD</strong> \u03c0\u03ac\u03bd\u03b5\u03bb 6,15 \u03b9\u03bd\u03c4\u03c3\u03ce\u03bd. \u0397 \u03b1\u03bd\u03ac\u03bb\u03c5\u03c3\u03b7 \u03c4\u03b7\u03c2 \u03bf\u03b8\u03cc\u03bd\u03b7\u03c2 \u03b5\u03af\u03bd\u03b1\u03b9 1080 x 2312 pixels (~415 ppi) \u03ba\u03b1\u03b9 \u03bf \u03bb\u03cc\u03b3\u03bf\u03c2 19,5:9, \u03c9\u03c2 \u03b5\u03af\u03b8\u03b9\u03c3\u03c4\u03b1\u03b9 \u03b4\u03b7\u03bb\u03b1\u03b4\u03ae \u03c3\u03c4\u03b1 P30 \u03ba\u03b1\u03b9 \u03c4\u03b1 \u03c0\u03b5\u03c1\u03b9\u03c3\u03c3\u03cc\u03c4\u03b5\u03c1\u03b1 premium \u03c4\u03b7\u03bb\u03ad\u03c6\u03c9\u03bd\u03b1. \u0397 \u03b1\u03bd\u03b1\u03c0\u03b1\u03c1\u03b1\u03b3\u03c9\u03b3\u03ae \u03c4\u03c9\u03bd \u03c7\u03c1\u03c9\u03bc\u03ac\u03c4\u03c9\u03bd \u03b5\u03af\u03bd\u03b1\u03b9 \u03b5\u03be\u03b1\u03b9\u03c1\u03b5\u03c4\u03b9\u03ba\u03ac \u03b1\u03ba\u03c1\u03b9\u03b2\u03ae\u03c2 \u03ba\u03b1\u03b9 \u03ba\u03b1\u03bb\u03cd\u03c0\u03c4\u03b5\u03b9 \u03bc\u03b9\u03ba\u03c1\u03ad\u03c2 \u03b1\u03b4\u03c5\u03bd\u03b1\u03bc\u03af\u03b5\u03c2 \u03cc\u03c0\u03c9\u03c2 \u03b7 \u03bc\u03b5\u03af\u03c9\u03c3\u03b7 \u03c4\u03b7\u03c2 \u03b1\u03bd\u03c4\u03af\u03b8\u03b5\u03c3\u03b7\u03c2 \u03c3\u03b5 \u03ba\u03ac\u03c0\u03bf\u03b9\u03b5\u03c2 \u03b3\u03c9\u03bd\u03af\u03b5\u03c2 \u03b8\u03ad\u03b1\u03c3\u03b7\u03c2 \u03ba\u03b1\u03b9 \u03c4\u03bf dimming \u03c3\u03c4\u03b9\u03c2 \u03ac\u03ba\u03c1\u03b5\u03c2. \u039f \u03bb\u03cc\u03b3\u03bf\u03c2 \u03b1\u03bd\u03c4\u03af\u03b8\u03b5\u03c3\u03b7\u03c2 \u03b5\u03af\u03bd\u03b1\u03b9 \u03c0\u03bf\u03bb\u03cd \u03c5\u03c8\u03b7\u03bb\u03ac (1231), \u03b1\u03ba\u03cc\u03bc\u03b1 \u03c8\u03b7\u03bb\u03cc\u03c4\u03b5\u03c1\u03b1 \u03b1\u03c0\u03cc \u03c4\u03b1 \u03ac\u03bb\u03bb\u03b1 P30.</p>\n<p>\u0397 \xab\u03ba\u03b1\u03c1\u03b4\u03b9\u03ac\xbb \u03c4\u03bf\u03c5 P30 Lite \u03b5\u03af\u03bd\u03b1\u03b9 \u03bf <strong>\u03bf\u03ba\u03c4\u03b1\u03c0\u03cd\u03c1\u03b7\u03bd\u03bf\u03c2 Hisilicon Kirin 710</strong> (12 nm). \u039c\u03b5 \u03bc\u03ad\u03b3\u03b9\u03c3\u03c4\u03b7 \u03c3\u03c5\u03c7\u03bd\u03cc\u03c4\u03b7\u03c4\u03b1 \u03bb\u03b5\u03b9\u03c4\u03bf\u03c5\u03c1\u03b3\u03af\u03b1\u03c2 \u03c4\u03b1 2.2 GHz, \u03bf Kirin 710 \u03b1\u03c0\u03bf\u03b4\u03b5\u03b9\u03ba\u03bd\u03cd\u03b5\u03c4\u03b1\u03b9 \u03c4\u03b1\u03c7\u03cd\u03c4\u03b1\u03c4\u03bf\u03c2 \u03ba\u03b1\u03b9 \u03b9\u03ba\u03b1\u03bd\u03cc\u03c4\u03b1\u03c4\u03bf\u03c2. \u0397 GPU \u03b5\u03af\u03bd\u03b1\u03b9 \u03b7 Mali-G51 MP4 \u03ba\u03b1\u03b9 \u03c4\u03bf P30 Lite \u03ba\u03c5\u03ba\u03bb\u03bf\u03c6\u03bf\u03c1\u03b5\u03af \u03c3\u03b5 \u03b4\u03cd\u03bf \u03b5\u03ba\u03b4\u03cc\u03c3\u03b5\u03b9\u03c2, 4GB \u03ae 6 GB.</p>\n<p>\u039c\u03b5 \u03c4\u03bf Performance mode \u03c4\u03bf \u03ba\u03b9\u03bd\u03b7\u03c4\u03cc \u03b1\u03c0\u03bf\u03b3\u03b5\u03b9\u03ce\u03bd\u03b5\u03c4\u03b1\u03b9 \u03ba\u03b1\u03b9 \u03bf\u03b9 \u03b5\u03c0\u03b9\u03b4\u03cc\u03c3\u03b5\u03b9\u03c2 \u03c4\u03bf\u03c5 \u03c3\u03c4\u03b1 benchmarks \u03b5\u03af\u03bd\u03b1\u03b9 \u03b1\u03c0\u03cc \u03c4\u03b9\u03c2 \u03c8\u03b7\u03bb\u03cc\u03c4\u03b5\u03c1\u03b5\u03c2 \u03c3\u03b5 \u03ba\u03ac\u03b8\u03b5 \u03ba\u03b1\u03c4\u03b7\u03b3\u03bf\u03c1\u03af\u03b1.</p>\n<p>\u039c\u03b5 \u03c4\u03b7\u03bd GPU TURBO 2.0 \u03bc\u03c0\u03bf\u03c1\u03b5\u03af\u03c2 \u03bd\u03b1 \u03c0\u03b1\u03c2 \u03c4\u03bf <strong>gaming</strong> \u03c3\u03b5 \u03ac\u03bb\u03bb\u03bf \u03b5\u03c0\u03af\u03c0\u03b5\u03b4\u03bf, \u03ba\u03b1\u03b8\u03ce\u03c2 \u03c3\u03bf\u03c5 \u03b5\u03be\u03b1\u03c3\u03c6\u03b1\u03bb\u03af\u03b6\u03b5\u03b9 \u03c3\u03c4\u03b1\u03b8\u03b5\u03c1\u03ae \u03b1\u03c0\u03cc\u03b4\u03bf\u03c3\u03b7 \u03ba\u03b1\u03b9 \u03c4\u03b1\u03c7\u03cd\u03c4\u03b7\u03c4\u03b1.</p>\n<p>\u03a4\u03bf P30 Lite \u03b4\u03b9\u03b1\u03c4\u03af\u03b8\u03b5\u03c4\u03b1\u03b9 \u03bc\u03b5 \u03c4\u03c1\u03b9\u03c0\u03bb\u03ae \u03c0\u03af\u03c3\u03c9 \u03ba\u03ac\u03bc\u03b5\u03c1\u03b1 \u03bc\u03b5 \u03b1\u03b9\u03c3\u03b8\u03b7\u03c4\u03ae\u03c1\u03b5\u03c2 48 MP (\u03b5\u03c5\u03c1\u03c5\u03b3\u03ce\u03bd\u03b9\u03bf\u03c2), 8 MP (\u03c5\u03c0\u03b5\u03c1\u03b5\u03c5\u03c1\u03c5\u03b3\u03ce\u03bd\u03b9\u03bf\u03c2) \u03ba\u03b1\u03b9 2 \u039c\u03a1 (\u03b1\u03b9\u03c3\u03b8\u03b7\u03c4\u03ae\u03c1\u03b1\u03c2 \u03b2\u03ac\u03b8\u03bf\u03c5\u03c2). \u039f \u03ad\u03be\u03c5\u03c0\u03bd\u03bf\u03c2 \u03b2\u03bf\u03b7\u03b8\u03cc\u03c2, \u03b4\u03b7\u03bb\u03b1\u03b4\u03ae \u03bf\u03b9 \u03b5\u03be\u03b5\u03bb\u03b9\u03b3\u03bc\u03ad\u03bd\u03bf\u03b9 \u03b1\u03bb\u03b3\u03cc\u03c1\u03b9\u03b8\u03bc\u03bf\u03b9 \u03c4\u03b5\u03c7\u03bd\u03b7\u03c4\u03ae\u03c2 \u03bd\u03bf\u03b7\u03bc\u03bf\u03c3\u03cd\u03bd\u03b7\u03c2, \u03b1\u03bd\u03b1\u03b3\u03bd\u03c9\u03c1\u03af\u03b6\u03b5\u03b9 \u03b1\u03c5\u03c4\u03cc\u03bc\u03b1\u03c4\u03b1 22 \u03c4\u03cd\u03c0\u03bf\u03c5\u03c2 \u03c3\u03ba\u03b7\u03bd\u03ce\u03bd \u03ba\u03b1\u03b9 \u03c1\u03c5\u03b8\u03bc\u03af\u03b6\u03b5\u03b9 \u03c4\u03b9\u03c2 \u03c0\u03b1\u03c1\u03b1\u03bc\u03ad\u03c4\u03c1\u03bf\u03c5\u03c2 \u03b1\u03bd\u03c4\u03af\u03c3\u03c4\u03bf\u03b9\u03c7\u03b1.</p>\n<p>\u0391\u03be\u03b9\u03bf\u03c0\u03bf\u03af\u03b7\u03c3\u03b5 \u03c4\u03bf hyper sampling lossless zoom \u03ba\u03b1\u03b9 \u03c4\u03b9\u03c2 \u03b4\u03c5\u03bd\u03b1\u03c4\u03cc\u03c4\u03b7\u03c4\u03b5\u03c2 \u03c4\u03bf\u03c5 48 MP \u03b1\u03b9\u03c3\u03b8\u03b7\u03c4\u03ae\u03c1\u03b1 \u03ba\u03b1\u03b9 \u03b5\u03c3\u03c4\u03af\u03b1\u03c3\u03b5 \u03c3\u03c4\u03b1 \u03c0\u03b9\u03bf \u03b1\u03c0\u03af\u03b8\u03b1\u03bd\u03b1 \u03c3\u03b7\u03bc\u03b5\u03af\u03b1 \u03b1\u03c0\u03cc \u03b5\u03bd\u03c4\u03c5\u03c0\u03c9\u03c3\u03b9\u03b1\u03ba\u03ad\u03c2 \u03b1\u03c0\u03bf\u03c3\u03c4\u03ac\u03c3\u03b5\u03b9\u03c2.</p>\n<p>\u039c\u03b5 \u03c4\u03bf\u03bd Ultra-Wide Angle 120\xb0 \u03c6\u03b1\u03ba\u03cc \u03c4\u03bf\u03c5 Huawei P30 Lite \u03bc\u03c0\u03bf\u03c1\u03b5\u03af\u03c2 \u03bd\u03b1 \u03b4\u03b7\u03bc\u03b9\u03bf\u03c5\u03c1\u03b3\u03ae\u03c3\u03b5\u03b9\u03c2 \u03c6\u03c9\u03c4\u03bf\u03b3\u03c1\u03b1\u03c6\u03af\u03b5\u03c2 \u03bc\u03b5 \u03c4\u03bf\u03c0\u03af\u03b1 \u03ba\u03b1\u03b9 \u03b1\u03bd\u03c4\u03b9\u03ba\u03b5\u03af\u03bc\u03b5\u03bd\u03b1 \u03b1\u03c0\u03cc \u03b3\u03c9\u03bd\u03af\u03b5\u03c2 \u03c0\u03bf\u03c5 \u03b4\u03b5\u03bd \u03b5\u03af\u03c7\u03b5\u03c2 \u03ba\u03b1\u03bd \u03c6\u03b1\u03bd\u03c4\u03b1\u03c3\u03c4\u03b5\u03af!</p>\n<p>\u03a3\u03b5 \u03cc,\u03c4\u03b9 \u03b1\u03c6\u03bf\u03c1\u03ac \u03c4\u03b1 \u03b2\u03af\u03bd\u03c4\u03b5\u03bf, \u03c4\u03bf P30 Lite \u03b1\u03c0\u03bf\u03b4\u03b5\u03b9\u03ba\u03bd\u03cd\u03b5\u03c4\u03b1\u03b9 \u03c0\u03bf\u03bb\u03cd \u03b9\u03ba\u03b1\u03bd\u03cc, \u03b4\u03af\u03bd\u03bf\u03bd\u03c4\u03b1\u03c2 \u03c0\u03bf\u03bb\u03cd \u03ba\u03b1\u03bb\u03cc \u03ba\u03b1\u03b9 \u03c3\u03c4\u03b1\u03b8\u03b5\u03c1\u03cc \u03b1\u03c0\u03bf\u03c4\u03ad\u03bb\u03b5\u03c3\u03bc\u03b1, \u03b5\u03b9\u03b4\u03b9\u03ba\u03ac \u03cc\u03c4\u03b1\u03bd \u03b5\u03c0\u03b9\u03bb\u03ad\u03be\u03b5\u03b9\u03c2 \u03c4\u03b7\u03bd \u03b1\u03bd\u03ac\u03bb\u03c5\u03c3\u03b7 1080p@30fps \u03c0\u03bf\u03c5 \u03b5\u03bd\u03b5\u03c1\u03b3\u03bf\u03c0\u03bf\u03b9\u03b5\u03af \u03b1\u03c5\u03c4\u03cc\u03bc\u03b1\u03c4\u03b1 \u03c4\u03bf \u03c3\u03cd\u03c3\u03c4\u03b7\u03bc\u03b1 \u03b7\u03bb\u03b5\u03ba\u03c4\u03c1\u03bf\u03bd\u03b9\u03ba\u03ae\u03c2 \u03c3\u03c4\u03b1\u03b8\u03b5\u03c1\u03bf\u03c0\u03bf\u03af\u03b7\u03c3\u03b7\u03c2 \u03b5\u03b9\u03ba\u03cc\u03bd\u03b1\u03c2 (\u0395.\u0399.S.).</p>\n<p>\u03a4\u03bf \u03bc\u03b5\u03b3\u03ac\u03bb\u03bf \u03c0\u03ac\u03bd\u03b5\u03bb \u03c4\u03b7\u03c2 \u03bf\u03b8\u03cc\u03bd\u03b7\u03c2 \u03c5\u03c0\u03bf\u03c3\u03c4\u03b7\u03c1\u03af\u03b6\u03b5\u03c4\u03b1\u03b9 \u03ba\u03b1\u03b9 \u03b1\u03c0\u03cc \u03bc\u03b9\u03b1 \u03bc\u03b5\u03b3\u03ac\u03bb\u03b7 \u03bc\u03c0\u03b1\u03c4\u03b1\u03c1\u03af\u03b1 3.340 mAh, \u03b1\u03bb\u03bb\u03ac \u03b7 \u03b5\u03be\u03bf\u03b9\u03ba\u03bf\u03bd\u03cc\u03bc\u03b7\u03c3\u03b7 \u03b5\u03bd\u03ad\u03c1\u03b3\u03b5\u03b9\u03b1\u03c2 \u03b3\u03af\u03bd\u03b5\u03c4\u03b1\u03b9 \u03ba\u03c5\u03c1\u03af\u03c9\u03c2 \u03b1\u03c0\u03cc \u03c4\u03bf\u03bd \u03b1\u03c0\u03bf\u03b4\u03bf\u03c4\u03b9\u03ba\u03cc \u03ba\u03b1\u03b9 \u03bf\u03b9\u03ba\u03bf\u03bd\u03bf\u03bc\u03b9\u03ba\u03cc Kirin 710. \u03a3\u03b5 \u03b4\u03b7\u03bc\u03bf\u03c3\u03b9\u03b5\u03c5\u03bc\u03ad\u03bd\u03b1 benchmarks \u03c4\u03bf P30 Lite \u03ad\u03c7\u03b5\u03b9 \u03bf\u03bc\u03b9\u03bb\u03af\u03b1 3G \u03c3\u03c7\u03b5\u03b4\u03cc\u03bd 29 \u03ce\u03c1\u03b5\u03c2 \u03ba\u03b1\u03b9 \u03c0\u03bb\u03bf\u03ae\u03b3\u03b7\u03c3\u03b7 \u03c3\u03c4\u03bf \u03b4\u03b9\u03b1\u03b4\u03af\u03ba\u03c4\u03c5\u03bf 14 \u03ce\u03c1\u03b5\u03c2 \u03ba\u03b1\u03b9 45 \u03bb\u03b5\u03c0\u03c4\u03ac. \u0393\u03b9\u03b1 \u03b1\u03ba\u03cc\u03bc\u03b1 \u03c0\u03b9\u03bf \u03be\u03ad\u03bd\u03bf\u03b9\u03b1\u03c3\u03c4\u03b7 \u03c7\u03c1\u03ae\u03c3\u03b7 \u03c5\u03c0\u03ac\u03c1\u03c7\u03b5\u03b9 \u03c0\u03c1\u03cc\u03b2\u03bb\u03b5\u03c8\u03b7 \u03b3\u03b9\u03b1 \u03c6\u03cc\u03c1\u03c4\u03b9\u03c3\u03b7 18 W \u03c0\u03bf\u03c5 \u03bc\u03c0\u03bf\u03c1\u03b5\u03af \u03bd\u03b1 \u03b1\u03bd\u03b1\u03c0\u03bb\u03b7\u03c1\u03ce\u03c3\u03b5\u03b9 \u03c4\u03bf 35% \u03c3\u03b5 \u03bc\u03b9\u03c3\u03ae \u03ce\u03c1\u03b1.</p>\n<p>\u039f\u03b9 \u03b2\u03b1\u03c3\u03b9\u03ba\u03ad\u03c2 \u03b4\u03b9\u03b1\u03c6\u03bf\u03c1\u03ad\u03c2 \u03bc\u03b5\u03c4\u03b1\u03be\u03cd \u03c4\u03c9\u03bd \u03b4\u03cd\u03bf \u03b5\u03af\u03bd\u03b1\u03b9 \u03cc\u03c4\u03b9 \u03c4\u03bf\nHuawei P30 lite \u03ad\u03c7\u03b5\u03b9 \u03c0\u03bb\u03b1\u03c3\u03c4\u03b9\u03ba\u03cc \u03c3\u03ba\u03b5\u03bb\u03b5\u03c4\u03cc, LCD \u03bf\u03b8\u03cc\u03bd\u03b7 \u03ba\u03b1\u03b9 \u03b5\u03c0\u03b5\u03be\u03b5\u03c1\u03b3\u03b1\u03c3\u03c4\u03ae Kirin 710. \u0391\u03bd\u03c4\u03af\u03c3\u03c4\u03bf\u03b9\u03c7\u03b1 \u03c4\u03bf P30 \u03ad\u03c7\u03b5\u03b9 \u03c3\u03ba\u03b5\u03bb\u03b5\u03c4\u03cc \u03b1\u03bb\u03bf\u03c5\u03bc\u03b9\u03bd\u03af\u03bf\u03c5, OLED \u03bf\u03b8\u03cc\u03bd\u03b7 \u03ba\u03b1\u03b9 \u03b5\u03c0\u03b5\u03be\u03b5\u03c1\u03b3\u03b1\u03c3\u03c4\u03ae Kirin 980. \u0391\u03be\u03af\u03b6\u03b5\u03b9 \u03bd\u03b1 \u03c3\u03b7\u03bc\u03b5\u03b9\u03c9\u03b8\u03b5\u03af \u03cc\u03c4\u03b9 \u03b7 \u03b2\u03b1\u03c3\u03b9\u03ba\u03ae \u03ba\u03ac\u03bc\u03b5\u03c1\u03b1 \u03c4\u03bf\u03c5 P30 lite \u03b5\u03af\u03bd\u03b1\u03b9 \u03b9\u03c3\u03c7\u03c5\u03c1\u03cc\u03c4\u03b5\u03c1\u03b7 (48\u039c\u03a1) \u03b1\u03c0\u03cc \u03c4\u03bf\u03c5 P30 (40\u039c\u03a1).</p>\n<p>\u03a4\u03bf <strong>Huawei P30 Lite</strong> \u03b5\u03af\u03bd\u03b1\u03b9 \u03bf\u03b9\u03ba\u03bf\u03bd\u03bf\u03bc\u03b9\u03ba\u03cc\u03c4\u03b5\u03c1\u03bf \u03c7\u03c9\u03c1\u03af\u03c2 \u03bd\u03b1 \u03b1\u03c0\u03bf\u03b3\u03bf\u03b7\u03c4\u03b5\u03cd\u03b5\u03b9 \u03c3\u03b5 \u03ba\u03b1\u03bd\u03ad\u03bd\u03b1\u03bd \u03c4\u03bf\u03bc\u03ad\u03b1 \u03c0.\u03c7. \u03b1\u03c5\u03c4\u03bf\u03bd\u03bf\u03bc\u03af\u03b1, \u03c6\u03c9\u03c4\u03bf\u03b3\u03c1\u03b1\u03c6\u03af\u03b1, \u03c4\u03b1\u03c7\u03cd\u03c4\u03b7\u03c4\u03b1 \u03bb\u03b5\u03b9\u03c4\u03bf\u03c5\u03c1\u03b3\u03af\u03b1\u03c2 \u03ba.\u03bb\u03c0. \u0391\u03bd \u03c3\u03c5\u03bd\u03c5\u03c0\u03bf\u03bb\u03bf\u03b3\u03af\u03c3\u03b5\u03b9\u03c2 \u03cc\u03c4\u03b9 \u03c0\u03c1\u03cc\u03ba\u03b5\u03b9\u03c4\u03b1\u03b9 \u03b3\u03b9\u03b1 \u03ad\u03bd\u03b1 \u03b1\u03c0\u03cc \u03c4\u03b1 \u03c0\u03b9\u03bf \u03cc\u03bc\u03bf\u03c1\u03c6\u03b1 \u03ba\u03b9\u03bd\u03b7\u03c4\u03ac \u03bc\u03b5 \u03c0\u03bb\u03b1\u03c3\u03c4\u03b9\u03ba\u03cc \u03c3\u03ba\u03b5\u03bb\u03b5\u03c4\u03cc \u03ba\u03b1\u03b9 \u03c4\u03bf \u03b3\u03b5\u03b3\u03bf\u03bd\u03cc\u03c2 \u03cc\u03c4\u03b9 \u03b4\u03cd\u03c3\u03ba\u03bf\u03bb\u03b1 \u03c4\u03bf \u03be\u03b5\u03c7\u03c9\u03c1\u03af\u03b6\u03b5\u03b9\u03c2 \u03b1\u03c0\u03cc \u03c4\u03bf P30 \u03ba\u03b1\u03b9 \u03c4\u03bf P30 Pro, \u03c4\u03cc\u03c4\u03b5 \u03ba\u03b1\u03c4\u03b1\u03bb\u03ae\u03b3\u03b5\u03b9\u03c2 \u03c3\u03c4\u03bf \u03c3\u03c5\u03bc\u03c0\u03ad\u03c1\u03b1\u03c3\u03bc\u03b1 \u03cc\u03c4\u03b9 \u03b5\u03af\u03bd\u03b1\u03b9 \u03ad\u03bd\u03b1 smartphone \u03c4\u03bf \u03bf\u03c0\u03bf\u03af\u03bf \u03b1\u03be\u03af\u03b6\u03b5\u03b9 \u03ba\u03ac\u03b8\u03b5 \u03b5\u03c5\u03c1\u03ce \u03c0\u03bf\u03c5 \u03b8\u03b1 \u03be\u03bf\u03b4\u03ad\u03c8\u03b5\u03b9\u03c2.</p>\n'}}}]);
